INSERT INTO `SERVMASK_PREFIX_posts` VALUES (549,1,'2021-05-11 12:59:49','2021-05-11 12:59:49','<!-- wp:paragraph -->\n<p>New modern Indian Cuisine<br>Die indische Küche erfreut sich mehr und mehr an exotischen Pastavariationen. Gerade in den Mega-<br>Metropolen Mumbai und Delhi finden italienische Restaurants eine große Anzahl an Fans und selbst<br>die Straßenküchen erfreuen sich einer immer stärkeren Beliebtheit an Pizza &amp; Pasta. Wir wollen<br>diesen Trend aus dem fernen Indien auch in unserem Restaurant in Mosbach aufnehmen und haben<br>speziell dafür zwei eigene Pasta Cross-Over Gerichte kreiert, die ab sofort bei uns bestellbar sind.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','New modern Indian Cuisine','','inherit','closed','closed','','349-revision-v1','','','2021-05-11 12:59:49','2021-05-11 12:59:49','',349,'http://burger-mahal.de/2021/05/11/349-revision-v1/',0,'revision','',0);
