INSERT INTO `SERVMASK_PREFIX_posts` VALUES (610,2,'2021-05-18 15:49:38','2021-05-18 15:49:38','<!-- wp:paragraph -->\n<p><strong>DAS INDISCHE HAUS feat. MOSBACHER TAPAS BAR</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Die neue Mosbacher Tapas Bar öffnet am Mittwoch, 19.07.2023 seine Pforten und ist künftig immer mittwochs und donnerstags von 16.00 bis 21.30 Uhr geöffnet. Entspannt im Biergarten einen Cocktail geniessen und dazu unsere hausgemachten Tapas regional - mediterran - international schlemmen. Gemütlich zum Feierabend entspannen oder einfach das Summer Feeling der warmen Tage aufsaugen. Einfach vorbei kommen und eine schöne Zeit bei uns verbringen. Wir freuen uns auf Euch.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":1225,\"width\":347,\"height\":228,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large is-resized\"><img src=\"https://burger-mahal.de/wp-content/uploads/2023/07/9A929EA7-7363-48CB-9B53-F3BC49DC62AE-1024x672.jpg\" alt=\"\" class=\"wp-image-1225\" width=\"347\" height=\"228\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":1226,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"https://burger-mahal.de/wp-content/uploads/2023/07/230705-Speisekarte-1.jpg\" alt=\"\" class=\"wp-image-1226\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','TOP AKTUELL INFORMIERT','','publish','closed','closed','','corona-pause-indian-summer','','','2023-07-19 06:34:47','2023-07-19 06:34:47','',0,'http://burger-mahal.de/?page_id=610',0,'page','',0);
INSERT INTO `SERVMASK_PREFIX_posts` VALUES (611,2,'2021-05-18 15:29:09','2021-05-18 15:29:09','<!-- wp:paragraph -->\n<p><strong>CORONA AUSZEIT BIS 31.Mai 2021 &amp; START INDIAN SUMMER AM 02.Juni 2021</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Aus aktuellem Anlass werden wir unser Restaurant bis zum 31.05.2021 schliessen. Wir<br>werden uns bis dahin auf den Indian Summer, der von Juni bis September dauern wird,<br>vorbereiten. Wir hoffen, dass bis dahin die Gastronomie wieder öffnen darf und wir Ihnen<br>etwas „Normalität“ bescheren dürfen. Seinen Sie gespannt auf unser neues Restaurant, den<br>neuen Biergarten und die neue Speisekarte in alt bekannter Frische, Tradition und Qualität.<br>Wir freuen uns auf Sie, bleiben Sie gesund<br>Das Indische Haus Team</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>INDIAN SUMMER - JUNI – SEPTEMBER 2021</strong><br><strong>OPEN: MITTWOCH – SONNTAG, START AM 02.JUNI 2021</strong></p>\n<!-- /wp:paragraph -->','CORONA PAUSE & INDIAN SUMMER','','inherit','closed','closed','','610-revision-v1','','','2021-05-18 15:29:09','2021-05-18 15:29:09','',610,'http://burger-mahal.de/?p=611',0,'revision','',0);
