INSERT INTO `SERVMASK_PREFIX_posts` VALUES (891,1,'2022-03-16 04:24:18','2022-03-16 04:24:18','<!-- wp:paragraph -->\n<p><strong>NEW MODERN INDIAN CUISINE</strong><br>Die indische Küche erfreut sich mehr und mehr an exotischen Pastavariationen. Gerade in den Mega-Metropolen Mumbai und Delhi finden italienische Restaurants eine große Anzahl an Fans und selbst die Straßenküchen erfreuen sich einer immer stärkeren Beliebtheit an Pizza &amp; Pasta. Wir wollen diesen Trend aus dem fernen Indien auch in unserem Restaurant in Mosbach aufnehmen und haben speziell dafür zwei eigene Pasta Cross-Over Gerichte kreiert, die ab sofort bei uns bestellbar sind.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"23px\"} -->\n<div style=\"height:23px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:paragraph -->\n<p><strong>CHICKEN PASTA „a la Chef“</strong><br>Gebratene Hähnchenbruststücke in einem pikanten Tomaten Curry mit Pasta Linguini, frischen Koriander und einem Hauch von Parmigiano Reggiano</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"25px\"} -->\n<div style=\"height:25px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:paragraph -->\n<p><strong>VEGGIE PALAK PASTA</strong><br>Pikantes Toamten - Spinat - Sahne Curry mit Pasta Linguini, frischen Koriander, kleinen Gemüsestücken, Kirschtomate und einem Hauch von Parmigiano Reggiano</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Wir freuen uns, ein weiteres Angebot für Sie zu haben und sind gespannt auf Ihre Meinung.</p>\n<!-- /wp:paragraph -->','Aktionen','','inherit','closed','closed','','349-revision-v1','','','2022-03-16 04:24:18','2022-03-16 04:24:18','',349,'https://burger-mahal.de/?p=891',0,'revision','',0);
